import React from "react";
import {data} from "../data.js"
import Navbar from "./Navbar.jsx"
import MovieCard from "./MovieCard.js"
import {addmovies,showfav}from "../actions"
import { render } from "@testing-library/react";

class  App extends React.Component {
  componentDidMount(){
    this.props.store.subscribe(()=>{
      // console.log("updated");
      this.forceUpdate();
    })
    this.props.store.dispatch(addmovies(data));
  }

  isFavourite=(movie)=>{

    const{favourites}=this.props.store.getState();
    const index=favourites.indexOf(movie);
    if(index==-1){
      return true;
    }
    else{
      return false;
    }

  }
  onChangeTab=(val)=>{
    this.props.store.dispatch(showfav(val))
  }

  render(){ 
    // pehle state array thi 
    // const movies=this.props.store.getState();
    // ab state object hai
    console.log("state=",this.props.store.getState());
    const{list,favourites,showFavourite}=this.props.store.getState();
    const displaymovies=showFavourite?favourites:list;
    return (
      <div className="main">   
      <Navbar/>
      <div className="main">
        <div className="tabs">
             <div className={`tab ${showFavourite?'':`active-tabs`}`} onClick={()=>this.onChangeTab(false)}>Movies</div>
             <div className={`tab ${showFavourite?`active-tab`:''}`} onClick={()=>this.onChangeTab(true)}>Favourites</div>

        </div>
        <div className="list">
             {displaymovies.map((movie,index)=>{
             return(
              <MovieCard  
              movie={movie} 
              key={index} 
              dispatch={this.props.store.dispatch}
              isFavourite={this.isFavourite(movie)}

              />
              )

             })}
        </div>
        {displaymovies.length==0?<div className="no-movies">No movies to display!</div>:null}
      </div>
      </div>


    );

  }

}

export default App;
